data.validators:
{
  sliderMin: {
    notEmpty (obj) {
      return _.trim(obj, '`')
    },
    number (value) {
      if (_.trim(value, '`').match(/^\`?\$\{.*(}`?)$/)) return true
      const val = value.replace(',', '.')
      return !!(_.trim(val, '`')).match(/^-?\d*\.?\d*$/);
    },
    moreThanLimit (obj) {
      if (_.trim(obj, '`').match(/^\`?\$\{.*(}`?)$/)) return true
      return _.trim(obj, '`').length <= 13
    },
    lessThanMin (obj) {
      if (_.trim(obj, '`').match(/^\$\{/)) return true
      if ((_.trim(obj, '`')).match(/\d+([\.|\,]?\d+)?$/)) {
        if (_.trim(obj, '`').includes('.')) {
          return _.trim(obj, '`').split('.')[1].length <= 4;
        } else if (_.trim(obj, '`').includes(',')) {
          return _.trim(obj, '`').split(',')[1].length <= 4;
        }
        return true
      } else {
        return true
      }
    }
  },
  sliderMax: {
    notEmpty (obj) {
      return _.trim(obj, '`')
    },
    moreThanLimit (obj) {
      if (_.trim(obj, '`').match(/^\$\{/)) return true
      return _.trim(obj, '`').length <= 13
    },
    number (obj) {
      if (_.trim(obj, '`').match(/^\`?\$\{.*(}`?)$/)) return true
      const val = obj.replace(',', '.')
      return !!(_.trim(val, '`')).match(/^-?\d*\.?\d*$/);
    },
    lessThanMin (obj) {
      if (_.trim(obj, '`').match(/^\$\{/)) return true
      if ((_.trim(obj, '`')).match(/\d+([\.|\,]?\d+)?$/)) {
        if (_.trim(obj, '`').includes('.')) {
          return _.trim(obj, '`').split('.')[1].length <= 4;
        } else if (_.trim(obj, '`').includes(',')) {
          return _.trim(obj, '`').split(',')[1].length <= 4;
        }
        return true
      } else {
        return true
      }
    }
  }
}

