data.componentLogic:
{
    computed : {
        getMarks () {
          if(validators.validateExpression(this.max) || validators.validateExpression(this.min)) return [];

          return [this.getMin, this.getMax].sort((a, b) => a - b);
        },
        getStep () {
          if(validators.validateExpression(this.step)) return 0;

          const formatted = _.isUndefined(this.codeModes.sliderStep) ? _.trim(this.step, '`').replace(',', '.') : _.trim(this.codeModes.sliderStep, ' "`\'');
          if (formatted === '' && (this.sliderDefaultValue || this.defaultValueStart)) {
            if ((this.sliderDefaultValue || this.defaultValueStart) - this.getMarks[0] > 10) {
              return 1;
            } else {
              return ((this.sliderDefaultValue || this.defaultValueStart) - this.getMarks[0]) / 100 || 0.01;
            }
          }

          if (formatted === '') {
            return 1;
          }

          if (formatted === 0 || formatted === '0' || formatted === 'undefined' || isNaN(formatted)) {
            if (this.getMarks[1] % 1 !== 0 || this.getMarks[0] % 1 !== 0) {
              if (this.getMarks[ 0 ] < 0.01) {
                let step = '0.';
                let i = 1;
                while (i < ('' + this.getMarks[ 0 ]).length - 2) {
                  step += '0';
                  i++;
                }
                step += '1';
                return step;
              }

              return 0.01;
            }
            return 1;
          }

          if ((this.getMarks[1] - this.getMarks[0]) % formatted === 0) {
            return Math.abs(formatted);
          }

          if ((this.getMarks[1] - this.getMarks[0]) % formatted !== 0) {
            return Math.abs(formatted);
          }

          if (!formatted.match(numberRegexp)) return 1; // if not only numbers

          return Math.abs(formatted > (this.getMarks[1] - this.getMarks[0]) / 2 ? 1 : parseInt(formatted));
        },
        getMin () {
          if(validators.validateExpression(this.min)) return 0;

          const numberRegexp = /^-?\d+\.?\d*$/mg;

          const formatted = _.isUndefined(this.codeModes.sliderMin) ? _.trim(this.min, '`').replace(',', '.') : _.trim(this.codeModes.sliderMin, ' "`\'');
          if (!formatted.match(numberRegexp)) return 0;
          if (+formatted % 1 !== 0) {
            return parseFloat((+formatted).toFixed(4));
          }
          return parseInt(formatted);
        },
        getMax () {
          if(validators.validateExpression(this.max)) return;

          const numberRegexp = /^-?\d+\.?\d*$/mg;

          const formatted = _.isUndefined(this.codeModes.sliderMax) ? _.trim(this.max, '`').replace(',', '.') : _.trim(this.codeModes.sliderMax, ' "`\'');
          if (!formatted.match(numberRegexp)) return 100;
          if (+formatted % 1 !== 0) {
            return parseFloat((+formatted).toFixed(4));
          }
          return parseInt(formatted);
        },
        getDefault() {
          if(validators.validateExpression(this.sliderDefaultValue)) return 0;

          const numberRegexp = /^-?\d+\.?\d*$/mg;

          const formatted = _.isUndefined(this.codeModes.sliderDefaultValue) ? _.trim(this.sliderDefaultValue, '` \'"').replace(',', '.') : _.trim(this.codeModes.sliderDefaultValue, ' "`\'');
          if (!formatted.match(numberRegexp)) return 100;
          if (+formatted % 1 !== 0) {
            return parseFloat((+formatted).toFixed(4));
          }
          return parseInt(formatted);
        },
        getDefaultStart() {
          if(validators.validateExpression(this.sliderDefaultStart)) return 0;

          const numberRegexp = /^-?\d+\.?\d*$/mg;

          const formatted = _.isUndefined(this.codeModes.sliderDefaultStart) ? _.trim(this.sliderDefaultStart, '` \'"').replace(',', '.') : _.trim(this.codeModes.sliderDefaultStart, ' "`\'');
          if (!formatted.match(numberRegexp)) return 100;
          if (+formatted % 1 !== 0) {
            return parseFloat((+formatted).toFixed(4));
          }
          return parseInt(formatted);
        },
        getDefaultEnd() {
          if(validators.validateExpression(this.sliderDefaultEnd)) return 0;

          const numberRegexp = /^-?\d+\.?\d*$/mg;

          const formatted = _.isUndefined(this.codeModes.sliderDefaultEnd) ? _.trim(this.sliderDefaultEnd, '` \'"').replace(',', '.') : _.trim(this.codeModes.sliderDefaultEnd, ' "`\'');
          if (!formatted.match(numberRegexp)) return 100;
          if (+formatted % 1 !== 0) {
            return parseFloat((+formatted).toFixed(4));
          }
          return parseInt(formatted);
        },
        sliderData() {
          if(validators.validateExpression(this.max) || validators.validateExpression(this.min) || validators.validateExpression(this.step))
            return [];


          if (Math.abs(this.getMax - this.getMin) % this.getStep !== 0 && this.getStep !== 0) {
            const mod = Math.abs(this.getMax - this.getMin) % this.getStep;
            let total = parseInt((Math.abs(this.getMax - this.getMin) - mod) / this.getStep);

            let step = null;
            if (total >= 1000) {
              total = 1000;
              step = parseFloat(Math.abs(this.getMax - this.getMin) / 1002).toFixed(4);
            }

            const start = Math.min(+this.getMin, +this.getMax);
            step =  step || +this.getStep;
            const toFixedNumber = this.getMarks[1] - this.getMarks[0] > 100 ? 0 : 4;

            let sliderData = Array.from(Array(total + 2), (_, i) => parseFloat((start + step * i).toFixed(toFixedNumber).replace(/.0000$/, '')));
            sliderData[sliderData.length - 1] = this.getMarks[1];

            return sliderData
          }

          return [];
        },

        showSinglePointWarning() {
          if(!this.sliderData || _.isEmpty(this.sliderData) || _.isEmpty(this.closestSingleSteps)) return false;
          if(this.sliderType !== `single`) return false;

          return !_.find(this.sliderData, (el => el === this.getDefault));
        },
        showDefaultStartWarning() {
          if(!this.sliderData || _.isEmpty(this.sliderData) || _.isEmpty(this.closestStartSteps)) return false;
          if(this.sliderType !== `range`) return false;

          return !_.find(this.sliderData, (el => el === this.getDefaultStart));
        },
        showDefaultEndWarning() {
          if(!this.sliderData || _.isEmpty(this.sliderData) || _.isEmpty(this.closestEndSteps)) return false;
          if(this.sliderType !== `range`) return false;

          return !_.find(this.sliderData, (el => el === this.getDefaultEnd));
        },

        showWarning() {
          return this.showSinglePointWarning || this.showDefaultStartWarning || this.showDefaultEndWarning;
        },

        closestSingleSteps() {
          return this.findClosestSteps(this.getDefault);
        },
        closestStartSteps() {
          return this.findClosestSteps(this.getDefaultStart);
        },
        closestEndSteps() {
          return this.findClosestSteps(this.getDefaultEnd);
        }
    },
    methods: {
      setDefaultValue(value, variable) {
        if(_.has(this.codeModes, variable)) {

          const codeModes = _.cloneDeep(this.codeModes);

          codeModes[variable] = `\`${value}\``;

          this.$emit(`update:codeModes`, codeModes);
          return;
        }

        this.$emit(`update:${variable}`, `\`${value}\``);
      },
      findClosestSteps(defaultValue) {
        if(!this.sliderData || _.isEmpty(this.sliderData)) return [];

        const closestIndex = _.findIndex(this.sliderData, el => el > defaultValue);

        if(closestIndex === -1) return [];

        if(closestIndex === 0) {
          return [this.sliderData[closestIndex]];
        }

        return [this.sliderData[closestIndex - 1], this.sliderData[closestIndex]]
      }
    },
    props : {
        sliderDefaultValue : {
            type : String,
            default : ''
        },
        sliderDefaultStart: {
          type: String,
          default: ''
        },
        sliderDefaultEnd: {
          type: String,
          default: ''
        },

        codeModes: {
          type: Object,
          default: () => ({})
        },

        min: {
          type: String,
          default: ''
        },
        max: {
          type: String,
          default: ''
        },

        step: {
          type: String,
          default: ''
        },
        sliderType: {
          type: String,
          default: ''
        },

        isNew : Boolean,
        readonly: {
            type : Boolean 
        },
        mergeFields: {
            type: Array
        },
        steps: {
            type: Array,
            default: () => {
                return []
            }
        },
        stepId: {
            type : String,
            default : ''
        }
    }
}

