              if (_.isEqual(this.value, res)) return;

              res.forEach(el => {
                if (!el.vforkey) {
                  el.vforkey = uuid.v4();
                }
                return el;
              }) // add key to prevent trigger change by or-list

              this.$emit('input', res);
            }
        },
        usedUrlInputs () {
          if (!_.get(this, 'value', []).length) return false;
          return this.value.every(el => el.linkUrl);
        }
    },
    props : {
        value : {
            type : String,
            default : ''
        },
        isNew : Boolean,
        readonly: {
            type : Boolean 
        },
        mergeFields: {
            type: Array
        },
