          type: Object,
          default() {
            return {schema: {}};
          }
        },
        stepId: {
            type : String,
            default : ''
        }
    },
    methods: {
      // media methods begin
      getValue (key, el) {
        return _.get(el || this.value, key);
      },
      updateMedias (field, value) {
        const medias = _.cloneDeep(this.medias);
        medias.forEach(el => {
          if (el.vforkey === field.vforkey) {
            el.linkUrl = field.linkUrl
            el.vforkey = field.vforkey
          }
          return el
        })
        this.medias = medias;
      },
      openModal (ref) {
        this.$refs[ref].open();
