    },
    methods: {
      openMapWindow () {
        const url = `${this.$flow.chatUiUrl}/set-pin`
        // const url = `http://localhost:8090/set-pin`
        try {
          this.defaultLocationWindow = window.open(url, 'default_location', 'width=600,height=600,top=0');
        } catch (e) {
          console.warn(e);
          this.defaultLocationWindow.close();
          this.defaultLocationWindow = window.open(url, 'default_location', 'width=600,height=600,top=0');
        }
      },
      frameMessageHandler (e) {
        if (e.data === 'PREVIEW_LOADED' && this.defaultLocationWindow) {
          this.sendMessageToPreview();
        }
        if (e.data.type === 'DATA_FROM_FRAME' && this.defaultLocationWindow) {
          this.defaultLocationWindow.close();
          const {lat, lng} = _.get(e, 'data.data.data')
          this.$emit('update:geolocationLat', '`' + lat + '`')
          this.$emit('update:geolocationLng', '`' + lng + '`')
        }
      },
      sendMessageToPreview () {
        if (!this.defaultLocationWindow) return;

        this.defaultLocationWindow.postMessage({
          type: 'changeSendBehavior',
          value: 'to-parent-window'
        }, '*');

        this.defaultLocationWindow.postMessage({
          type: 'messageData',
          message: {
            message: 'Set default pin position when user blocks geolocation tracking.',
            medias: [],
            answerComponent: {
              component: 'rwc-location',
              vBind: {
                buttonLabel: 'Pick location',
                defaultLat: this.geolocationLat,
                defaultLng: this.geolocationLng
              },
              showComponentInMessage: true
            }
          }
        }, '*');

        const config = _.get(this.conversation, 'meta.lookAndFeel')
        this.defaultLocationWindow.postMessage({
          type: 'chatConfig',
          config: {
            ...config
          }
        }, '*');
      }
    }
}

