//function that describe how to serialize wildcard data 
 function toJson(data) { 
   if (/\$\{.+?\}/.test(data)) return data;
 if (_.isArray(data)) { 
    return `[${_.map(data, toJson).join(',')}]`;
    }
    if (_.isObject(data)) {
        return `{${_.map(data, (value, key) => `${key}: ${toJson(value)}`).join(',')}}`;
    }
    return JSON.stringify(data);
}

return _.mapValues(data, toJson);

data.validators:
{
  videoStartTimeTimestring: {
    notValid (value) {
      if (!_.trim(value, '`')) return true
      if (_.trim(value, '`').match(/^\s*$/)) return false
      try {
        libs.timestring(value, 'ms')
        if (_.trim(value, '`') == 0) return true
        if (!value) return true
        if (/\$\{/.test(value)) return true
        if(!(value.slice(1, -1) || '').match(/^[a-z0-9\s]+$/gi)) return false
        if ((value.slice(1, -1) || '').match(/\`\$\"\#\?\\\!\@\#\$\%\^\&\*\)\_\+\=\-\'\]\/\[\{\}/gi)) return false
        const regex = /^(([0-9]|[0-9][0-9]|[0-1][0-9][0-9]|60)\s?[ss].*|([0-9]|[0-9][0-9]|[0-1][0-9][0-9]|60)\s?[mM].*|([0-9]|[0-3][0-9]|4[0-8])\s?[hH].*|(01-9]|[0-9]\w+?)\s?[dD].*)$/;
        return regex.test(value.slice(1, -1));
      } catch (e) {
        if (/\$\{/.test(value)) return true
        return false
      }
    },
    moreThanDuration (value) {
      if (this.schema.videoUsedLinks || !this.schema.videoTotalDuration || !_.trim(value, '`')) return true
      try {
        const val = libs.timestring(value, 'ms')
        const total = this.schema.videoTotalDuration * 1000
        return val <= total
      } catch (e) {
        console.warn(e)
        return false
      }
    }
  }
}

