{
    computed : {
        computedValue : {
            get () { return this.value; },
            set (value) { this.$emit('input', value); }
        }
    },
    props : {
        flow: {
          type: Object,
          default: () => ({})
        },
        value : {
            type : String,
            default : ''
        },
        isNew : Boolean,
        readonly: {
            type : Boolean
        },
        mergeFields: {
            type: Array
        },
        buttonStyle: String,
        textInputCustomPattern: String,
        textInputType: String,
        conversation: Object,
        rwcContext: {
          type: Object,
          default: () => ({})
        },
        textMessage: String,
        inputComponent: String,
        textPlaceholder: String,
        schema: Object,
        cardBtnLabel: String,
        cardDescription: String,
        medias: Array,
        steps: {
            type: Array,
            default: () => {
                return []
            }
        },
        stepId: {
            type : String,
            default : ''
        }
    },
    mounted () {
      window.addEventListener('message', this.previewLoadedHandler, false);
    },
    data() {
      return {
        loading: false,
        btnDisabled: true,
        configError: false
      }
    },
    watch: {
      schema: {
        handler () { this.sendMessageToPreview() },
        deep: true
      },
      rwcContext: {
        handler(nv) {
          this.configError = false
        },
        deep: true
      },
      'flow.store.state.flow.lastActionTime': {
        immediate: true,
        handler () {
          if(!this.readonly) {
            this.btnDisabled = false;
            return;
          }

          this.checkIsFlowActive();
        }
      }
    },
    methods: {
      async checkIsFlowActive (immediate) {
        this.loading = true;

        if (!this.flow.id) {
          this.btnDisabled = false;
          return;
        };

        try {
          const deployments = await this.flow.api.deployments.getActiveDeploymentsByFlowId(this.flow.id);
          this.btnDisabled = deployments.length === 0;        
        } finally { this.loading = false; }
      },
      openPreviewWindow () {
        const url = `${this.$flow.chatUiUrl}/preview`
        // const url = `http://localhost:8090/preview`
        try {
          this.previewWindow = window.open(url, 'preview', 'width=600,height=600,top=0');
        } catch (e) {
          console.warn(e);
          this.previewWindow.close();
          this.previewWindow = window.open(url, 'preview', 'width=600,height=600,top=0');
        }
      },
      previewLoadedHandler (e) {
        if (e.data === 'PREVIEW_LOADED' && this.previewWindow) {
          this.sendMessageToPreview();
        }
      },
      getReadableMergeTag (value = '', options) {
        const mergeTagLeftWrapper = _.get(options, 'mergeTagLeftWrapper', '`{');
        const mergeTagRightWrapper = _.get(options, 'mergeTagRightWrapper', '}`');
        if (value) {
          return _.chain(value)
            .replace(/^[`'"](.*)[`'"]$/g, '$1')
            .replace(/await this\.mergeFields\[[`'"](.*)[`'"]\]\.get\((.*path: *[`'"](.*)[`'"])?.*\)/g, (str, $1, $2, $3) => `${mergeTagLeftWrapper}${$1}${$3 ? `.${$3}` : ''}${mergeTagRightWrapper}`) // eslint-disable-line max-params
            .replace(/this\.get\([`'"](.*)[`'"]\)/g, `${mergeTagLeftWrapper}$1${mergeTagRightWrapper}`)
            .replace(/this\.session\.get\([`'"](.*)[`'"]\)/g, `${mergeTagLeftWrapper}#session.$1${mergeTagRightWrapper}`)
            .replace(/await this\.getShared\([`'"](.*)[`'"]\)/g, `${mergeTagLeftWrapper}#shared.$1${mergeTagRightWrapper}`)
            .replace(/await this\.getGlobal\([`'"](.*)[`'"]\)/g, `${mergeTagLeftWrapper}#global.$1${mergeTagRightWrapper}`)
            .replace(/this\.(.+)/, `${mergeTagLeftWrapper}#$1${mergeTagRightWrapper}`)
            .value();
        }
        return '';
      },
      mapDataToClient (inputComponent) {
        const componentName = _.trim(this.inputComponent, '`')
        const isHasMessage = ['card', 'carousel'].indexOf(componentName) === -1
        const isHasMedias = ['textFiles'].indexOf(componentName) !== -1
        let showComponentInMessage = [
          'schedule',
          'checkbox', 
          'datepicker', 
          'dropdown', 
          'menu', 
          'radio', 
          'timepicker', 
          'attachments', 
          'geolocation',
          'slider',
          'signature',
          'video'
        ].indexOf(componentName) !== -1

        const componentMap = {
          schedule: {
            name: 'rwc-scheduler',
            buttonLabel: 'scheduleButtonLabel',
            options: 'scheduleOptionItems',
            allowMultiselect: 'scheduleAllowMultiselect',
            multiselectQuantity: 'scheduleMultiselectQuantity',
            workingHours: 'workingHours',
            disableByFunction: 'scheduleDisableByFunction',
            disableByMf: 'scheduleDisableByMf',
            disableByManually: 'scheduleDisableByManually',
            disableFunction: 'scheduleDisableFunction',
            disableMf: 'scheduleDisableMf',
            disableManual: 'scheduleDisableManual'
          },
          credit: {
            name: 'rwc-credit',
            buttonLabel: 'creditBtnLabel',
            cancelButtonLabel: 'creditCancelBtnLabel',
            termsCheckboxLabel: 'creditTermsLabel',
            termsCheckboxLink: 'creditTerms'
          },
          carousel: {
            name: 'rwc-carousel',
            options: 'carouselOptions',
            hasOtherOption: 'carouselHasAlternative',
            otherOptionData: 'carouselAlternative',
            submitBtnLabel: 'carouselSubmitBtnLabel',
            cancelBtnLabel: 'carouselCancelBtnLabel',
            multiple: 'carouselMultiselect'
          },
          checkbox: {
            name: 'rwc-checkbox-group',
            options: 'checkboxOptions',
            defaultValue: 'checkboxDefault'
          },
          datepicker: {
            name: 'rwc-datepicker',
            outFormat: 'datepickerFormat',
            startDay: 'datepickerStartsFrom',
            isBlockedDates: 'datepickerBlockDates',
            isSuggestedDates: 'datepickerSuggestedDates',
            blockedOptions: 'datepickerBlockedOptions',
            suggestedOptions: 'datepickerSuggestedOptions',
            blockedFunction: 'datepickerBlockedFunc',
            suggestedFunction: 'datepickerSuggestedFunc'
          },
          dropdown: {
            name: 'rwc-dropdown',
            options: 'dropdownOptions',
            dropdownPlaceholder: 'dropdownPlaceholder'
          },
          slider: {
            name: 'rwc-slider',
            min: 'sliderMin',
            max: 'sliderMax',
            step: 'sliderStep',
            unit: 'sliderUnit',
            unitPosition: 'sliderUnitPosition',
            mode: 'sliderType',
            useCustomLabels: 'sliderUseCustomLabels',
            labelFunction: 'sliderCustomFunction',
            options: 'sliderCustomLabels',
            defaultValue: 'sliderDefaultValue',
            defaultValueStart: 'sliderDefaultStart',
            defaultValueEnd: 'sliderDefaultEnd'
          },
          signature: {
            name: 'rwc-signature',
            btnLabel: 'signatureBtn'
          },
          menu: {
            name: 'rwc-menu',
            options: 'optionListOptions'
          },
          buttons: {
            name: 'rwc-buttons-list',
            options: 'optionsButtons'
          },
          radio: {
            name: 'rwc-radio-group',
            options: 'radioOptions',
            defaultValue: 'radioDefault'
          },
          rate: {
            name: 'rwc-rate',
            ratePlaceholder: 'ratePlaceholder',
            showInput: 'rateInput',
            ratingSubmitLabel: 'ratingSubmitLabel',
            ratingDiscardLabel: 'ratingDiscardLabel'
          },
          textFiles: {
            name: 'or-textbox',
            placeholder: 'textPlaceholder',
            allowMask: 'textInputAllowMask'
          },
          timepicker: {
            name: 'rwc-timepicker',
            timeFormat: 'timepickerFormat'
          },
          video: {
            name: 'rwc-video',
            startTime: 'videoStartTime',
            autoplay: 'videoAutoplay',
            button: 'videoBtnLabel',
            video: 'videoSrc',
            poster: 'videoPoster'
          },
          attachments: {
            name: 'rwc-attachment',
            isMulti: 'attachmentsNumber',
            filesValidation: 'attachmentsAllowChosen',
            allowedFileTypes: 'attachmentsAllowedTypes'
          },
          geolocation: {
            name: 'rwc-location',
            buttonLabel: 'geolocationBtn',
            useDefault: 'geolocationSetDefault',
            defaultLat: 'geolocationLat',
            defaultLng: 'geolocationLng'
          },
          custom: {
            name: 'custom',
            templateType: 'customTemplateType',
            component: 'customComponent',
            showBotIcon: 'showBotIcon',
            scripts: 'customTempalateScripts',
            autoSubmit: 'autoSubmitCustomTemplate',
            html: 'customTemplateHtmlCode',
            javascript: 'customTemplateJavaScriptCode',
            css: 'customTemplateCSSCode',
            vars: 'customVariables'
          }
        }


        const answerParams = _.mapValues(componentMap[componentName], (el) => { 
          if (el === 'scheduleDisableManual') {
            return this.schema[el].map(evt => {
              return {
                ...evt,
                dateFrom: evt.dateFrom ? JSON.stringify(evt.dateFrom) : '',
                dateTo: evt.dateTo ? JSON.stringify(evt.dateTo) : ''
              }
            })
          }

          const codeModeValue = _.get(this.schema, `__codeModes.${el}`);

          return _.isUndefined(codeModeValue) ? this.schema[el] : this.parseCodeModeValue(codeModeValue);
        });


        const answerComponent = {
          component: componentMap[componentName].name,
          vBind: answerParams,
          showComponentInMessage
        }

        return { answerComponent, isHasMessage, isHasMedias }
      },
      async sendMessageToPreview () {
        if (!this.previewWindow) return;

        const { answerComponent, isHasMessage, isHasMedias } = this.mapDataToClient(_.trim(this.inputComponent, '`'))


        if (answerComponent.vBind.component) {
          try {
            const result = await new Function(answerComponent.vBind.component)()
            answerComponent.vBind.component = JSON.stringify(result, function(key, value) {
              if (typeof value === 'function') {
                return _.toString(value)
              } else {
                return value;
              }
            });
            answerComponent.showComponentInMessage = _.get(result, 'showComponentInMessage');

            const showComponentInMessageLocal = _.get(result, 'showComponentInMessage');

            answerComponent.showComponentInMessage = _.isUndefined(showComponentInMessageLocal) ? this.schema.showCustomComponentInMessage : showComponentInMessageLocal;
          } catch (e) {
            console.warn(e)
            answerComponent.vBind.component = ""
          }

          // console.log('COMP', answerComponent.vBind.component)
          // answerComponent.vBind.component = answerComponent.vBind.component.toString()
          // console.log('COMP', answerComponent.vBind.component)
          // answerComponent.vBind.component = answerComponent.vBind.component.replace(/{\n? * ?path.*\n*.*}/gi, (match) => {
          //   return match.replace(/\n */gi, '')
          // })

          // const strWithoutMergeTags = this.getReadableMergeTag(answerComponent.vBind.component)

          // answerComponent.vBind.component = JSON.stringify(new Function(strWithoutMergeTags).call(this), function(key, value) {
          //   if (typeof value === 'function') {
          //     return _.toString(value)
          //   } else {
          //     return value;
          //   }
          // });

          // if (this.inputComponent === '`custom`') {
          //   answerComponent.showComponentInMessage = _.get(JSON.parse(answerComponent.vBind.component), 'showComponentInMessage')
          //   answerComponent.waitForAnswer = _.get(JSON.parse(answerComponent.vBind.component), 'waitForAnswer')
          // }
        }

        if (this.inputComponent !== '`textFiles`') {
          answerComponent.vBind.allowMask = false
        }

        const codeModeTextMessageValue = _.get(this.schema, '__codeModes.textMessage');
        const codeModeMediasValue = _.get(this.schema, '__codeModes.medias');
        const codeModeSuggestionsValue = _.get(this.schema, '__codeModes.autoSuggestionOptionsList');
        const codeModeSuggestionType = _.get(this.schema, '__codeModes.autoSuggestionType');
        const codeModeSuggestionsFromMergeField = _.get(this.schema, '__codeModes.autoSuggestionOptionsMergeField');
        const codeModeIsSuggestionInput = _.get(this.schema, '__codeModes.autoSuggestion');


        const textMessage = _.isUndefined(codeModeTextMessageValue) ? this.textMessage : this.parseCodeModeValue(codeModeTextMessageValue, codeModeTextMessageValue);
        const medias = _.isUndefined(codeModeMediasValue) ? this.medias : this.parseCodeModeValue(codeModeMediasValue, []);

        const oldOptions = _.get(this.schema, 'autoSuggestion.options', []);
        const newOptions = _.get(this.schema, 'autoSuggestionOptionsList', []);
        const suggestions = _.isUndefined(codeModeSuggestionsValue) ? _.isEmpty(oldOptions) ? newOptions : oldOptions : this.parseCodeModeValue(codeModeSuggestionsValue, []);

        const suggestionsType = _.isUndefined(codeModeSuggestionType) ? _.get(this.schema, 'autoSuggestionType') : this.parseCodeModeValue(codeModeSuggestionType, `emty`);
        const suggestionsFromMergeField = _.isUndefined(codeModeSuggestionsFromMergeField) ? _.get(this.schema, 'autoSuggestionOptionsMergeField') : this.parseCodeModeValue(codeModeSuggestionsFromMergeField, ``);
        const isSuggestionInput = _.isUndefined(codeModeIsSuggestionInput) ? _.get(this.schema, 'autoSuggestion.input') : this.parseCodeModeValue(codeModeIsSuggestionInput, {}).input;


        this.previewWindow.postMessage({
          type: 'messageData',
          message: {
            message: isHasMessage ? textMessage : '',
            medias: isHasMedias ? medias : [],
            answerComponent,
            suggestions,
            suggestionsType,
            suggestionsFromMergeField,
            isSuggestionInput,
            placeholder: this.textPlaceholder,
            textInputType: this.textInputType,
            textInputCustomPattern: this.textInputCustomPattern
          }
        }, '*');


        // const config = _.get(this.conversation, 'meta.lookAndFeel')
        const ctxHelpers = this.$flow.getCache('rwcContext.helpers');

        if (!ctxHelpers) {
          this.configError = true
          return;
        }

        const config = ctxHelpers.getLastStepWithContext('manual', 'data.conversation.meta.lookAndFeel')
         || ctxHelpers.getInitialStep('data.dataOut.meta.lookAndFeel');

        // to show warning message in template
        if (!config) {
          this.configError = true
        } else {
          this.configError = false
        }
        this.previewWindow.postMessage({
            type: 'chatConfig',
            config: {
              ...config
            }
          }, '*');
      },
      parseCodeModeValue(value, defaultValue = []) {
        try { return eval(`(${value})`); }
        catch(e) { return defaultValue; }
      }
    }
}

