      uniq(val, list) {
        list = _.get(this, 'schema.autoSuggestionOptionsList', list);
        const currentText = _.get(val, 'text', '');

        const duplicates = _.filter(list, ({ text }) => _.trim(text) === _.trim(currentText));

        return _.size(duplicates) < 2;
      }
    }
  }
}

