data.componentLogic:
{
    data () {
      return {
        previewWindow: null,
        allowedFileTypes: ['mov', 'MOV', 'mp4', 'MP4'],
        maxFiles: 1
      };
    },
    computed : {
        authToken () { return this.$settings.token },
        medias : {
            get () {
              return this.value;
            },
            set (value) {
              let res = value;
              if (_.isEqual(this.value, res)) return;

              res.forEach(el => {
                if (!el.vforkey) {
                  el.vforkey = uuid.v4();
                }
                return el;
              }) // add key to prevent trigger change by or-list

              this.$emit('input', res);
            }
        },
        usedUrlInputs () {
          if (!_.get(this, 'value', []).length) return false;
          return this.value.every(el => el.linkUrl);
        }
    },
    props : {
        value : {
            type : String,
            default : ''
        },
        isNew : Boolean,
        readonly: {
            type : Boolean 
        },
        mergeFields: {
            type: Array
        },
        steps: {
            type: Array,
            default: () => {
                return []
            }
        },
        v : {
          type: Object,
          default() {
            return {schema: {}};
          }
        },
        stepId: {
            type : String,
            default : ''
        }
    },
    methods: {
      // media methods begin
      getValue (key, el) {
        return _.get(el || this.value, key);
      },
      updateMedias (field, value) {
        const medias = _.cloneDeep(this.medias);
        medias.forEach(el => {
          if (el.vforkey === field.vforkey) {
            el.linkUrl = field.linkUrl
            el.vforkey = field.vforkey
          }
          return el
        })
        this.medias = medias;
      },
      openModal (ref) {
        this.$refs[ref].open();
      },
      removeSelectedItem (key) {
        const _medias = this.value
        _medias.splice(key, 1);
        medias = _medias
        // this.setValue('medias', _medias);
      },
      addMedia () {
        if (_.has(_.get(this.value, '[0]'), 'linkUrl')) {
          const _medias = this.value;
          _medias.push(this.addNewUrl());
          this.medias = _medias
          // this.setValue('medias', _medias);
        } else {
          this.openModal('select-media');
        }

      },
      addNewUrl () {
        return {
          linkUrl: "``",
          vforkey: uuid.v4()
        }
      },
      changeMediaToUrl () {
        const _medias = [this.addNewUrl()];
        this.medias = _medias
        // this.setValue('medias', _medias);
      }
    }
}

