stepTemplates.template:
let dataOut = {};
let parseValue = (elem, force) => {
  if (force) return _.isString(elem) ? elem : JSON.stringify(elem)
  if (!_.isEmpty(_.trim(elem)) && _.isFinite(Number(elem))) elem = Number(elem);
  if (elem === "true") elem = true;
  if (elem === "false") elem = false;
  if (elem === "null") elem = null;
  if (elem === "undefined") elem = undefined;
  return elem;
}

let recur = (obj, virtualValue) => {
  let res;
  if (_.isArray(obj)) {
    res = _.map(obj, (elem, key) => {
      if (_.isArray(elem) || _.isObject(elem)) {
        return recur(elem, _.find(virtualValue.value, vitem => _.isEqual(vitem.key, key)))
      } else {
        return parseValue(_.replace(_.replace(elem, /\\\\/g, "\\"), /\\`/g, '`'), _.find(virtualValue.value, vitem => _.isEqual(vitem.key, key)) ? _.find(virtualValue.value, vitem => _.isEqual(vitem.key, key)).force : false);
      }
    });
  } else {
    res = _.mapValues(obj, (elem, key) => {
      if (_.isArray(elem) || _.isObject(elem)) {
        return recur(elem, _.find(virtualValue.value, vitem => _.isEqual(vitem.key, key)))
      } else {
        return parseValue(_.replace(_.replace(elem, /\\\\/g, "\\"), /\\`/g, '`'), virtualValue ? _.find(virtualValue.value, vitem => _.isEqual(vitem.key, key)).force : false);
      }
    })
  }
  return res;
};


if (selectedMode === 'Advanced') {
  _.map(Object.keys(nativeJson), item => {
    let virtualVal = _.find(virtualJson, vitem => vitem.key === item);
    if (_.find(virtualJson, vitem => vitem.key === item) && _.find(virtualJson, vitem => vitem.key === item).force) {
      let val = nativeJson[item];
      _.set(dataOut, item, _.isString(val) ? val : JSON.stringify(val));
    } else {
      let val = nativeJson[item];
      if (_.isObject(nativeJson[item]) || _.isArray(nativeJson[item])) {
        _.set(dataOut, item, recur(val, virtualVal));
      } else {
        val = _.replace(_.replace(val, /\\\\/g, "\\"), /\\`/g, '`');
        if (!_.isEmpty(_.trim(nativeJson[item])) && _.isFinite(Number(nativeJson[item]))) val = Number(nativeJson[item]);
        if (nativeJson[item] === "true") val = true;
        if (nativeJson[item] === "false") val = false;
        if (nativeJson[item] === "null") val = null;
        if (nativeJson[item] === "undefined") val = undefined;
        _.set(dataOut, item, val);
      }
    }
  });
} else if (selectedMode === 'Extended') {
  variables.forEach(item => {
    if (item.variableName === '') throw new Error("Property name can't be empty.");

    switch (_.get(item, 'valueType')) {
      case 'string':
        _.set(dataOut, item.variableName, String(item.variableValue))
        break
      case 'number':
        if (_.isEmpty(item.variableValue) || _.isEmpty(item.variableValue.trim())) {
          throw new Error("Value can't be empty.")
        }
        if (!_.isFinite(_.toNumber(item.variableValue))) {
          throw new Error(`Incorrect data type was used for ${item.variableName}`)
        }
        _.set(dataOut, item.variableName, _.toNumber(item.variableValue))
        break
      case 'boolean':
        if (!_.isBoolean(item.variableValue)) {
          throw new Error(`Incorrect data type was used for ${item.variableName}`)
        }
        _.set(dataOut, item.variableName, item.variableValue)
        break
      case 'null':
        _.set(dataOut, item.variableName, null)
        break
      default:
        _.set(dataOut, item.variableName, item.variableValue)
        break
    }
  });

} else if (selectedMode === 'Simple') {
  if (valueType === 'code' && typeof simpleData === 'undefined') {
    throw new Error("Value type of merge field data is 'undefined', please enter a valid data type!");
  }

  if (valueType === 'number' && (_.isEmpty(simpleData) || _.isEmpty(simpleData.trim()))) throw new Error("Value can't be empty.");

  if ((valueType === 'number' && !_.isFinite(_.toNumber(simpleData))) ||
    (valueType === 'boolean' && !_.isBoolean(simpleData))) {
    throw new Error(`Incorrect data type was used for Merge Field`);
  }

  dataOut = valueType === 'number' ?
    Number(simpleData) : valueType === 'boolean' ?
    Boolean(simpleData) : valueType === 'null' ?
    null : simpleData;
}

return this.exitStep('next', dataOut);

