data.validators:
{
  simpleData : {
    custom(value) {
      if(this.schema.selectedMode !== 'Simple') return true;

      let containsMergeField = _.isString(value) && validators.validateExpression(value);

      if (/await|this/.test(value) && this.schema.valueType === 'number') {
        return true
      }

      if (this.schema.valueType === 'code' && !validators.jsExpression(value)){
        return false;
      }

      if(
        this.schema.valueType === 'number' && !value.slice(1,-1).trim().match(/[\+\-]?\d+(\.\d+)?/) || 
        this.schema.valueType === 'number' && !(value.slice(1,-1) && !_.isEmpty(value.slice(1,-1)) && 
        (containsMergeField || _.isFinite(_.toNumber(value.slice(1,-1)))))
      ){
        return false;
      }

      return true;
    }
  },
  variableName : {
     custom(value) {
        return !_.isEmpty(this.schema.dataOut);
      }
  }
}

