          type: Boolean,
          default: false
        },
        isNew: Boolean
      },

      computed: {
        invalidValue() {
          const invalid = (this.valueTypeLocal === 'number' && !this.isValidNumber) || 
            (this.valueTypeLocal === 'code' && !validators.jsExpression(this.variableValueLocal)) ||
            (this.valueTypeLocal === 'code' && this.variableValueLocal === '')

          if (invalid) {
            this.errorText = this.valueTypeLocal === 'number' 
              ? 'Invalid number.' 
              : 'Please enter a valid JS expression.';
          }

          if (this.valueTypeLocal === 'number') {
            this.checkNumber()
          }

          return invalid
        },

        isNull () {
          return this.valueTypeLocal === 'null';
        },

        isValidNumber () {
          const checked =  this.checkNumber();

          let containsMergeField = validators.validateExpression(this.variableValue);

          return checked || this.variableValue.slice(1,-1) && 
            !_.isEmpty(this.variableValue.slice(1,-1)) && (_.isFinite(_.toNumber(this.variableValue.slice(1,-1))) || containsMergeField) && 
            this.variableValue.slice(1,-1).trim().match(/[\+\-]?\d+(\.\d+)?/);
        },

        variableNameLocal: {
          get() {
            return this.variableName;
          },
          set(newValue) {
