          get() {
            return this.variableValue;
          },
          set(newValue) {
            this.$emit('update:variableValue', newValue.trim ? newValue.trim() : newValue);
          }
        },

        valueTypeLocal: {
          get() {
            return this.valueType;
          },
          set(newValue) {
            if (newValue === 'string' || newValue === 'number') {
              this.variableValueLocal = '``'
            } else if (newValue === 'boolean') {
              this.variableValueLocal =  _.isBoolean(this.variableValueLocal) ? this.variableValueLocal : true;
            } else if (newValue === 'null') {
              this.variableValueLocal = '`null`';
