        if (this.valueType === 'string' && this.variableValueLocal === '') {
          this.variableValueLocal = '``'
        }

        this.$root.$on('validateVariable', itemId => {
          if (itemId !== this._uid) this.validateVariableName(this.variableName, true);
        });

        if(!this.isNew && _.isEmpty(this.variableName)){
          this.errorText = 'Value is required.';
          this.isInvalidVariableName = true;
        }

        // if(this.$refs.variableValueInput) this.isTextInput = this.$refs.variableValueInput.displayTextInput;
        // this.isInvalidVariableName = _.isEmpty(this.variableNameLocal) && !this.isNew;
      },

      template: `<%= variableItem %>`
    }
  },

  data(){
    return {
      variableName: this.mergeField.name || '',
      variableValue: _.isBoolean(this.simpleData) ? this.simpleData : (this.simpleData || ''),
      valueType: 'string'
    }
  },

  watch: {
    variableName() {
      const mergeField = _.assign(this.mergeField, {name : this.variableName});
      this.$emit('update:mergeField', mergeField);
      const currentIndexStep = _.findIndex(this.steps, item => item.id === this.stepId);

      if(currentIndexStep !== -1) {
        this.steps[currentIndexStep]['outputExample'] = {};

        this.$emit('update:simpleData', this.variableValue)


      }
    },

    variableValue(v) {
      const currentIndexStep = _.findIndex(this.steps, item => item.id === this.stepId);
      if(currentIndexStep !== -1) {
        this.steps[currentIndexStep]['outputExample'] = {};

        this.$emit('update:simpleData', this.variableValue)
      }
    },

    selectedMode:{
      handler(newVal){
        if(_.isEqual(newVal, 'Simple')){
          this.variableName = this.mergeField.name;
          const currentIndexStep = _.findIndex(this.steps, item => item.id === this.stepId);
          if(currentIndexStep !== -1) {
            this.steps[currentIndexStep]['outputExample'] = {};
            this.$emit('update:simpleData', this.variableValue)
          }
        }
      }
    } 
  },

  mounted(){
    if (this.selectedMode !== 'Simple') return 

    this.variableName = this.mergeField.name;
    const currentIndexStep = _.findIndex(this.steps, item => item.id === this.stepId);

    if(currentIndexStep !== -1) {
      this.steps[currentIndexStep]['outputExample'] = {};
      this.$emit('update:simpleData', this.variableValue)
    }
  },

  props : {
    mergeField: {
      type: String,
      default: ''
    },
    mergeFields: {
      type: Array,
      default () {
        return [];
      }
    },
    outputExample: {
      type: Object,
      default () { return {} }
    },
    step:Object,
    simpleData: String,
    schema:{
      type: Object,
      default : () => {} 
    },
    selectedMode : {
      type : String,
      default : 'string'
    },
    v: {
      type: Object,
      default () {
        return {
          schema: {
            variables: {}
          }
        }
      }
    },
    steps: '',
    stepId: '',
    readonly: {
      type: Boolean,
      default: false
    },
    isNew:Boolean
  }
}

data.componentOriginalStyles:
.simple-mode {
  margin-bottom: 30px;
}

.or-code {
  min-height: 100%;
}

.empty-list {
  color: #91969D;	
  font-size: 12px;
  line-height: 18px;
}

.variables-list {
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  margin-bottom: 30px;

  .or-list-items {
    width: 100%;
  }

  > button.ui-button.ui-button--type-secondary.ui-button--color-primary {
  	font-size: 14px;
  	line-height: 14px;
  }  

  &.or-list {

    .list-item {
      position: relative;
      padding: 0 36px 0 0;

      > .remove-button {
      	position: absolute;
        right: 0;
        top: 36px;
      }

      > .handle {
        display: none;
      }
    }

    &.single-item {

      .list-item {
        padding-left: 0;
      }
    }
  }
}

.variable {
  width: 100%;
  display: flex;
  .ui-select__content{
    z-index:3;
  }
  &__name {
    width: 50%;
    padding-right: 21px;

    .ui-textbox {
      &.is-invalid .ui-textbox__feedback-text {
        visibility: visible;
      }
    }
  }

  &__value {
    width: 100%;

    .input-wrapper {
      position: relative;

      &:after {
        content: '';
        position: absolute;
        top: 18px;
        left: -15px;
        color: #0F232E;
        font-size: 14px;
        transform: translateY(-50%);
      }
    }

    .ui-select {
      &__dropdown {
        top: 100%;
        right: 0;
        width: auto;
        min-width: auto;
      }

      &__options {
        min-width: auto;
      }
    }

    .ui-select,
    .or-text-expression {
      margin-bottom: 0;  
    }

    .or-text-expression.invalid .or-code .ace-wrapper {
      border-color: #f95d5d;
    }

    .ui-select__label {
    	display: flex;
    	justify-content: space-between;
    }

    .ui-select .ui-select__content .ui-select__label .ui-select__display {
      width: auto;
      padding: 2px 0 2px 10px;
      border: 0;
      background: transparent;
    }

    .ui-select__display-value, .ui-select__dropdown-button {
        flex-grow: 0;
    }

    .ui-select__display-value, .ui-select__dropdown-button {
      color: #64b2da !important;
    }

    .ui-select .ui-select__content .ui-select__label .ui-select__label-text {
      white-space: nowrap;
      font-size: 14px;
    }

    .ui-select.has-label .ui-select__dropdown-button {
      top: 0px;
      font-size: 20px;
      margin-top: 2px;
    }

    .ui-radio-group {
      &__label-text {
        display: flex;
        align-items: center;
        flex-grow: 1;
        font-size: 12px;
        line-height: 36px;
        color: rgb(145, 150, 157);
        text-overflow: ellipsis;
        white-space: nowrap;
        overflow: hidden;
      }

      &__radio {
        &:not(:first-child) {
          margin-left: 10px;
        }
      }

      &__radios {
        position: relative;

        &:after {
          content: '';
          position: absolute;
          top: 18px;
          left: -15px;
          color: #0F232E;
          font-size: 14px;
          transform: translateY(-50%);
        }
      }

      &:not(.is-disabled):not(.is-invalid):hover .ui-radio-group__label-text,
      &:not(.is-disabled):not(.is-invalid).is-active .ui-radio-group__label-text {
        color: #91969d;
      }
    }

    .ui-radio .ui-radio__label-text {
      font-size: 14px;
      line-height: 24px;
    }

    .ui-radio-group__radios {
      display: flex;
      justify-content: left;
      height: 36px;
      font-size: 14px;
      border-radius: 3px;
      padding: 7px 10px;
      background: #f6f6f6;
    }
  }
}

.value-type-helper {
  display: block;
  color: rgba(0,0,0,.38);
  line-height: 1.2;
  font-size: 12px;
}

.or-text-expression {
  position: relative;
  flex-grow: 1;
  min-width: 50%;
  .ui-icon-button.ui-icon-button--type-secondary.ui-icon-button--color-default.ui-icon-button--size-normal.flat.js-mode-btn.active{
    top:2px;
    right:26px;
  }
    & >.header {
    position: absolute;
    height: 36px;
    width: 100%;
    min-height: 0;
    top: -36px;
    & >button.ui-icon-button.flat.add-variable {
      visibility: visible;
      position: absolute;
      top: 38px;
      right: 0;
      z-index: 2;
    }
  }

  .input-wrapper {
    width: 100%;
  }

  .js-mode-btn {
    visibility: hidden;
    position: absolute;
    top: -2px;
    right: 75px;
    z-index: 2;
  }


  .editable {
      width: calc(100% - 36px);
      padding-right: 0;
      overflow: hidden;
  }
  .ace_scroller {
      width: calc(100% - 72px);
      padding-right: 0;
      overflow: hidden;
  }
}
.select-label {
    color: rgba(0, 0, 0, 0.54);
    font-size: 12px;
    line-height: 36px;
    transform-origin: left;
    transition: color .1s ease,transform .2s ease;
}
.select-wrapper {
  display: flex;
  justify-content: space-between;
}

.valueTypeSelect {
  margin-right: 30px;
}

.js-mode-btn {
  display: none;
}

data.toJson:
//function that describe how to serialize wildcard data 
 function toJson(localData, key) { 
 if (_.isArray(localData)) { 
    return `[${_.map(localData, toJson).join(',')}]`;
    }
    if (_.isObject(localData)) {
      return `{${_.map(localData, (value, key) => `${key}: ${toJson(value, key)}`).join(',')}}`;
    }

    if(_.isEqual('variableValue',key) && data.selectedMode === 'Extended'){
      return localData;
    }
    return JSON.stringify(localData);
}

return _.mapValues(data, toJson);

