data.componentLogic:
{
  components: {
    variableItem: {
      props: {
        variableNameLable: {
          type: String,
          default: ''
        },
        variableName: {
          type: String,
          default: ''
        },
        variableValue: {
          default: ''
        },
        valueType: {
          type: String,
          default: 'string'
        },
        variables: {
          type: Array,
          default () {
            return [];
          }
        },
        isTextInput: {
          type: Boolean,
          default: true
        },
        steps: '',
        stepId: '',
        mergeFields: {
          type: Array,
          default () {
            return [];
          }
        },
        readonly: {
          type: Boolean,
          default: false
        },
        isNew:Boolean
      },

      computed: {
        invalidValue(){
          const invalid = (this.valueTypeLocal === 'number' && !this.isValidNumber) || 
            (this.valueTypeLocal === 'code' && !validators.jsExpression(this.variableValueLocal)) ||
            (this.valueTypeLocal === 'code' && this.variableValueLocal === '')

          if (invalid) {
            this.errorText = this.valueTypeLocal === 'number' 
              ? 'Invalid number.' 
              : 'Please enter a valid JS expression.';
          }

          if (this.valueTypeLocal === 'number') {
            this.checkNumber()
          }

          return invalid
        },

        isNull () {
          return this.valueTypeLocal === 'null';
        },

        isValidNumber(){
          const checked =  this.checkNumber();

          let containsMergeField = validators.validateExpression(this.variableValue);

          return checked || _.isFunction(this.variableValue.slice) && 
            (this.variableValue.slice(1,-1) && !_.isEmpty(this.variableValue.slice(1,-1)) && (_.isFinite(_.toNumber(this.variableValue.slice(1,-1))) || containsMergeField) && 
            this.variableValue.slice(1,-1).trim().match(/[\+\-]?\d+(\.\d+)?/));
        },

        variableNameLocal: {
          get() {
            return this.variableName;
          },
          set(newValue) {
            this.$emit('update:variableName', newValue);
            this.validateVariableName(newValue);
          }
        },
        variableValueLocal: {
          get() {
            return this.variableValue;
          },
          set(newValue) {
            this.$emit('update:variableValue', newValue);
          }
        },
        valueTypeLocal: {
          get() {
            return this.valueType;
          },

          set(newValue, oldValue) {
            if (newValue === 'string' || newValue === 'number') {
              this.variableValueLocal = '``'
            } else if (newValue === 'boolean') {
              this.variableValueLocal =  _.isBoolean(this.variableValueLocal) ? this.variableValueLocal : true;
            } else if (newValue === 'null') {
              this.variableValueLocal = '`null`';
            } else if (newValue === 'code') {
              this.variableValueLocal = ''
            }

            this.$emit('update:valueType', newValue);
          }
        }
      },

      data () {
        return {
          variableTypeOptions: ['string', 'number', 'boolean', 'null', 'code'],
          isInvalidVariableName: false,
          errorText: 'Invalid number',
          errorLabelText: 'Property name is required.',
          isTextInput: true
        }  
      },

      watch: {
        variables() {
          this.$root.$emit('validateVariable');
        },
        isTextInput: {
          handler(newVal){
              this.errorText = newVal ? 'Invalid number': 'Please enter a valid JS expression.';
          }
        }
      },

      methods: { 
        checkNumber () {
          if (/await|this/.test(this.variableValue)) return true

          this.errorText =  _.isFunction(this.variableValue.slice) && this.variableValue.slice(1,-1).trim() === '' ? 'Value is required.' : 'Invalid number'

          if (_.isFunction(this.variableValue.slice) && /^\s+$/.test(this.variableValue.slice(1,-1))) {
            this.errorText = 'Value is required.'
          }

          return false
