            this.isInvalidVariableName = true;
          } else {
            let variables = _.clone(this.variables);
            let duplicates = variables.map(item => item.variableName)
                                    .filter((item, index, arr) => arr.lastIndexOf(item) == index && arr.indexOf(item)!= index);

            if (duplicates.indexOf(varName) > -1) {
              this.errorLabelText = 'Use another property name.';
              this.isInvalidVariableName = true;
            }
          }

          if(!isFromEmit) this.$root.$emit('validateVariable', this._uid);
        },
        toggleInputMode () {
          if(_.get(this, '$refs.variableValueInput.toggleMode')) this.$refs.variableValueInput.toggleMode()
        }
      },

      mounted () {
        if (this.valueType === 'code') {
          this.toggleInputMode()
        }

        if (this.valueType === 'string' && this.variableValueLocal === '') {
          this.variableValueLocal = '``'
        }

        this.$root.$on('validateVariable', itemId => {
          if (itemId !== this._uid) this.validateVariableName(this.variableName, true);
        });

        if(this.$refs.variableValueInput) this.isTextInput = this.$refs.variableValueInput.displayTextInput;
        this.isInvalidVariableName = _.isEmpty(this.variableNameLocal) && !this.isNew;
      },

      template: `<%= variableItem %>`
    }
  },

  watch: {
    variables: {
      handler (newValue) {
        if (this.selectedMode === 'Extended') {
          const currentIndexStep = _.findIndex(this.steps, item => item.id === this.stepId);

          if(currentIndexStep !== -1) {
            const outputExample = {};
            _.forEach(newValue, item => {
              _.set(outputExample, `${item.variableName}`, item.variableValue);
            });
            this.steps[currentIndexStep]['outputExample'] = outputExample;
          }
          this.$emit('input', newValue); 
        }
      },
      deep: true
    },
    selectedMode:{
      handler(newVal){
        if (newVal === 'Extended') {
          const currentIndexStep = _.findIndex(this.steps, item => item.id === this.stepId);

          if(currentIndexStep !== -1) {
            const outputExample = {};
            _.forEach(this.variables, item => {
              _.set(outputExample, `${item.variableName}`, item.variableValue);
            });
            this.steps[currentIndexStep]['outputExample'] = outputExample;
          }
          this.$emit('input', this.variables); 

        }
      }
    } 
  },

  data () {
    return {
      variables: this.value,
      newVariable () {
        return {
          variableName: '',
          variableValue: '``',
          valueType: 'string'
        };
      }
    }  
  },

  async mounted () {
    await this.$nextTick();
    if (this.selectedMode === 'Extended') {
      const currentIndexStep = _.findIndex(this.steps, item => item.id === this.stepId);

      if(currentIndexStep !== -1) {
        const outputExample = {};
        _.forEach(this.variables, item => {
          _.set(outputExample, `${item.variableName}`, item.variableValue);
        });
        this.steps[currentIndexStep]['outputExample'] = outputExample;
      }
      this.$emit('input', this.variables); 

      if (this.variables.length === 0 && _.get(this, '$refs.variablesOrList.addItem')) {

        this.$refs.variablesOrList.addItem();
      }
    }
  },

  props: {
    value: {
      type: Array,
      default () {
        return [];
      }
    },
    v: {
      type: Object,
      default () {
        return {
          schema: {
            variables: []
          }
        }
      }
    },
    selectedMode: '',
    mergeFieldName: {},
    steps: '',
    stepId: '',
    mergeFields: {
      type: Array,
      default () {
        return [];
      }
    },
    readonly: {
      type: Boolean,
      default: false
    },
    isNew:Boolean
  }
}

