data.validators:
virtualJson : {
  custom(value) {
    if(this.schema.selectedMode !== 'Advanced') return true;
    let tempObj = {
      value,
      type:"object"
    }
    function isJsonValid(node){
      let res = true;
      if(node.type === "object"){
        const checker = _.chain(node.value)
                    .map('key')
                    .filter((elem) => !_.isEmpty(_.trim(elem)))
                    .uniq()
                    .value()
                    .length
        if(checker !== _.get(node, 'value.length', 0)) {
          res = false;
        }
        _.forEach(node.value, n => {
          res = res && isJsonValid(n);
        });
      }
      if(node.type === "array"){
        _.forEach(node.value, n => {
          res = res && isJsonValid(n);
        });
      }
      return res;
    }

    return isJsonValid(tempObj);
  }
}

