stepTemplates.template:
if (!gotoStepId) {
  throw new Error('no goto step id');
}

if (loopDetection) {
  const stepId = this.currentStepId;
  let limit = this.get(`limit_${stepId}`, numberOfPasses);
  let parsedLimit;

  let firstEnterLocal = this.get(`firstEnter_${stepId}`, firstEnter);
  this.set(`firstEnter_${stepId}`, false);

  if (!_.isNumber(limit) && !_.isString(limit)) {
    if (handleLimit) {
      this.log.error(`The Limit is invalid: ${limit}`);
      return this.exitStep('limit');  
    }
    throw new Error(`The Limit is invalid: ${limit}`);
  }

  parsedLimit = _.parseInt(limit);

  if ((_.isNaN(parsedLimit) || parsedLimit < 1) && firstEnterLocal) {
    if (handleLimit) {
      this.log.error(`The Limit is invalid: ${limit}`);
      return this.exitStep('limit');
    }
    throw new Error(`The Limit is invalid: ${limit}`);
  }

  if (parsedLimit >= 1) {
    parsedLimit -= 1;
    this.set(`limit_${stepId}`, parsedLimit);
    return this.jumpTo(gotoStepId);
  } else if (handleLimit) {
    return this.exitStep('limit');
  } else {
    throw new Error(`Limit of Go To step is exceeded.`);
  }
} else {
  return this.jumpTo(gotoStepId);
}

