data.componentLogic:
{ 
  props : {
    gotoStepId : {
      type : String,
      default : '``'
    },
    stepId : String,
    steps : {
      type : Array,
      default () {
        return [];
      }
    },
    readonly : {
      type : Boolean,
      default : false
    },
    selectedStep : {
      type : Object,
      default () {
        return {};
      }
    },
    v : {
      type: Object,
      default() {
        return {
          schema: {
            gotoStepId: {},
            selectedStep: {}
          }
        };
      }
    }
  },

  data() {
    return {
      options : this.fillOptions(),
      query : '',
      brokenRefernceError: 'Broken reference: the selected step is detached from the main flow tree.',
      requiredFieldError: 'Destination step is required.'
    }
  },

  computed: {
    selectedStepId: {
      get() {
        this.highlightDestinationOn(this.gotoStepId);
        return this.gotoStepId;
      },

      set(selectedStepId) {
        this.highlightDestinationOff(this.selectedStepId);
        const selectedStep = _.find(this.options, { value: selectedStepId });
        this.$emit('update:gotoStepId', selectedStepId);
        if (selectedStep) {
          this.$emit('update:selectedStep', selectedStep);
        }
        this.highlightDestinationOn(selectedStepId);
      }
    },
    errorMessage() {
      let errorMessage = '';
      if (this.v.schema.gotoStepId.$error) {
        errorMessage = this.brokenRefernceError;
      }

      if (this.v.schema.selectedStep.$error) {
        errorMessage = this.requiredFieldError;
      }
      return errorMessage;
    }
  },

  methods : {
    highlightDestinationOn(stepId) {
      stepId = stepId.slice(1, -1);
      this.$flow.highlightStepOn({stepId, highlightClass : 'hover'});
    },

    highlightDestinationOff(stepId) {
      stepId = stepId.slice(1, -1);
      this.$flow.highlightStepOff({stepId, highlightClass : 'hover'});  
    },

    highlightDestinationOffOnMouseout(stepId) {
      if (stepId !== this.selectedStepId) {
        stepId = stepId.slice(1, -1);
        this.$flow.highlightStepOff({stepId, highlightClass : 'hover'});  
      }
    },

    zoomInOnStep () {
      const stepId = this.selectedStepId.slice(1, -1);
      this.$flow.zoomInOnStep(stepId);
    },

    fillOptions() {
      const thisStep = _.find(this.steps, {id : this.stepId}) || {};
      const steps =  _.chain(this.steps)
        .filter(step => step.id !== thisStep.id && step.type !== 'empty' && step.type !== thisStep.type)
        .map(step => ({ value: `\`${step.id}\``, label : step.label || 'No name' }))
        .value();
      const rememberedStep = _.clone(this.selectedStep);
      const selectedStep = _.find(steps, {value : rememberedStep.value});

      if (rememberedStep.label && !selectedStep) {
        steps.push(rememberedStep);
      } else if (rememberedStep && selectedStep && rememberedStep.label !== selectedStep.label){
        this.$emit('update:selectedStep', selectedStep);
      }

      return _.sortBy(steps, step => step.label.toLowerCase());
    }
  },

  watch : {
    steps : {
      handler() {
        this.options = this.fillOptions();
      }, 
      deep : true
    }
  },

  beforeDestroy () {
    this.highlightDestinationOff(this.selectedStepId);
  },

  created () {
    const selectedStep = _.find(this.options, {value : this.selectedStepId});
    if (!selectedStep) {
      this.selectedStepId = '``';
    } else {
      this.highlightDestinationOn(this.selectedStepId);
    }
  }
}

