data.componentLogic:
{
  data() {
    return {
      exitsLocal: _.clone(this.exits),
      errorMessageInCodeMode: 'The Limit of redirects is not a valid JavaScript expression.',
      errorMessageRequired: 'The Limit of redirects is required.',
      errorMessageInTextMode: 'Only positive number can be used as a value.'
    }
  },
  computed: {
    errorMessage() {
      let message = '';
      switch (this.textExpressionMode) {
        case 'text': 
          const mergfieldRegex = /\$\{this.get\('.{1,}'\)\}/g;
          let num = this.numberOfPassesLocal.slice(1, -1);
          if (_.isEmpty(num)) {
            message = this.errorMessageRequired;
          } else if (!mergfieldRegex.test(num) || _.isNaN(parseInt(num))) {
            message = this.errorMessageInTextMode;
          }
          break;
        case 'code': 
          if (_.isEmpty(this.numberOfPassesLocal)) {
            message = this.errorMessageRequired;
          } else if (!validators.jsExpression(this.numberOfPassesLocal)) {
            message = this.errorMessageInCodeMode;
          }
          break;
      }

      return message;
    },
    loopDetectionLocal: {
      get () { 
        return this.loopDetection; 
      },
      set (value) {
        if (!value) {
          this.handleLimitLocal = false;
        }
        this.$emit('update:loopDetection', value);
      }
    },
    handleLimitLocal: {
      get () { 
        return this.handleLimit;
      },
      set (value) {
        if (!_.isEmpty(this.stepId)) {
          const limitLegIndex =  _.findIndex(this.exitsLocal, { label : 'limit' });
          if (!value && limitLegIndex >= 0) {
            this.exitsLocal.splice(limitLegIndex, 1);
          } else if (value && limitLegIndex === -1) {
            this.exitsLocal.splice(0, 0, {
              id : uuid.v4(),
              label : 'limit',
              stepId : '',
              dynamic : true
            });
          }
        }

        this.$emit('update:handleLimit', value);
      }
    },
    numberOfPassesLocal: {
      get () { return this.numberOfPasses; },
      set (value) { this.$emit('update:numberOfPasses', value); }
    }
  },
  methods: {
    setTextExpressionMode(mode) {
      this.$emit('update:textExpressionMode', mode)
    },
    highlightDestinationOn() {
      const stepId = this.gotoStepId.slice(1, -1);
      this.$flow.highlightStepOn({stepId, highlightClass : 'hover'});
    }
  },
  watch : {
    exitsLocal : {
      handler (value) {
        this.$emit('update:exits', value);
      }
    }
  },
