function filterTagIds(tagCollection: string[] = [], tagIdsToFilter: string[]): { newIds: string[]; existingIds: string[]; } {
  let newIds: string[] = [];
  const existingIds: string[] = [];

  if (!tagCollection.length) {
    newIds = tagIdsToFilter;
  } else {
    tagIdsToFilter.forEach(tagId => {
      if (tagCollection.indexOf(tagId) !== -1) {
        existingIds.push(tagId);
      } else {
        newIds.push(tagId);
      }
    });
  }

  return {
    newIds,
    existingIds,
  };
}

export default filterTagIds;
